var btnbuyLink=document.querySelector(".product-list-catalog-grid"),buyLink=document.querySelector(".btn-buy"),basketPopup=document.querySelector(".popup-basket"),basketClose=basketPopup.querySelector(".close-popup-basket");btnbuyLink.addEventListener("click",function(e){e.preventDefault(),e.target.classList.value.includes("btn-buy")&&basketPopup.classList.add("modal-show")}),basketClose.addEventListener("click",function(e){e.preventDefault(),basketPopup.classList.remove("modal-show")}),window.addEventListener("keydown",function(e){27===e.keyCode&&basketPopup.classList.contains("modal-show")&&(e.preventDefault(),basketPopup.classList.remove("modal-show"))});
